import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext';
import styles from './DashboardPage.module.css';

const DashboardPage = () => {
    const { user } = useContext(AuthContext);
    const [stats, setStats] = useState({
        totalQuestions: 0,
        multipleChoiceQuestions: 0,
        openEndedQuestions: 0,
        recentQuestions: []
    });
    const [loading, setLoading] = useState(true);

    // Fetch dashboard data on mount
    useEffect(() => {
        const fetchDashboardData = async () => {
            try {
                // Fetch all questions for the user
                const response = await axios.get('/api/questions');
                const questions = response.data;

                // Calculate stats
                const multipleChoiceQuestions = questions.filter(q => q.question_type === 'multiple_choice');
                const openEndedQuestions = questions.filter(q => q.question_type === 'open_ended');

                // Get 5 most recent questions
                const recentQuestions = [...questions]
                    .sort((a, b) => new Date(b.created_at) - new Date(a.created_at))
                    .slice(0, 5);

                setStats({
                    totalQuestions: questions.length,
                    multipleChoiceQuestions: multipleChoiceQuestions.length,
                    openEndedQuestions: openEndedQuestions.length,
                    recentQuestions
                });
            } catch (error) {
                console.error('Error fetching dashboard data:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchDashboardData();
    }, []);

    // Format date for display
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    if (loading) {
        return (
            <div className={styles.loadingContainer}>
                <p>Loading dashboard...</p>
            </div>
        );
    }

    return (
        <div className={styles.container}>
            <h1 className={styles.title}>Dashboard</h1>

            <div className={styles.welcomeCard}>
                <h2>Welcome, {user?.email}!</h2>
                <p>You have <span className={styles.creditsHighlight}>{user?.credits}</span> credits remaining.</p>
                {user?.credits < 5 && (
                    <Link to="/credits" className={styles.buyCreditsButton}>
                        Buy More Credits
                    </Link>
                )}
            </div>

            <div className={styles.statsGrid}>
                <div className={styles.statsCard}>
                    <div className={styles.statsNumber}>{stats.totalQuestions}</div>
                    <div className={styles.statsLabel}>Total Questions</div>
                </div>

                <div className={styles.statsCard}>
                    <div className={styles.statsNumber}>{stats.multipleChoiceQuestions}</div>
                    <div className={styles.statsLabel}>Multiple Choice</div>
                </div>

                <div className={styles.statsCard}>
                    <div className={styles.statsNumber}>{stats.openEndedQuestions}</div>
                    <div className={styles.statsLabel}>Open Ended</div>
                </div>
            </div>

            <div className={styles.actionsCard}>
                <h2>Quick Actions</h2>
                <div className={styles.actionButtons}>
                    <Link to="/upload" className={styles.actionButton}>
                        Upload New Question
                    </Link>
                    <Link to="/history" className={styles.actionButton}>
                        View Question History
                    </Link>
                    <Link to="/credits" className={styles.actionButton}>
                        Manage Credits
                    </Link>
                </div>
            </div>

            <div className={styles.recentQuestionsCard}>
                <div className={styles.cardHeader}>
                    <h2>Recent Questions</h2>
                    <Link to="/history" className={styles.viewAllLink}>
                        View All
                    </Link>
                </div>

                {stats.recentQuestions.length === 0 ? (
                    <div className={styles.emptyState}>
                        <p>You haven't uploaded any questions yet.</p>
                        <Link to="/upload" className={styles.uploadButton}>
                            Upload Your First Question
                        </Link>
                    </div>
                ) : (
                    <div className={styles.recentQuestionsList}>
                        {stats.recentQuestions.map((question) => (
                            <div key={question.id} className={styles.questionItem}>
                                <div className={styles.questionDetails}>
                                    <div className={styles.questionText}>
                                        {question.question_text.length > 100
                                            ? `${question.question_text.substring(0, 100)}...`
                                            : question.question_text}
                                    </div>
                                    <div className={styles.questionMeta}>
                                        <span className={styles.questionType}>
                                            {question.question_type === 'multiple_choice' ? 'Multiple Choice' : 'Open Ended'}
                                        </span>
                                        <span className={styles.questionDate}>
                                            {formatDate(question.created_at)}
                                        </span>
                                    </div>
                                </div>
                                <Link to={`/history`} className={styles.viewButton}>
                                    View
                                </Link>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default DashboardPage;
